/* synlex_destroy is for both reentrant and non-reentrant scanners. */
int synlex_destroy  (void)
{
    
    /* Pop the buffer stack, destroying each element. */
	while(YY_CURRENT_BUFFER){
		syn_delete_buffer(YY_CURRENT_BUFFER  );
		YY_CURRENT_BUFFER_LVALUE = NULL;
		synpop_buffer_state();
	}

	/* Destroy the stack itself. */
	synfree((yy_buffer_stack) );
	(yy_buffer_stack) = NULL;

    return 0;
}

