int get_next_char(int *ch)
{
	char buf[1];
 	int result;

	YY_INPUT(buf, result, 1);
	
	*ch = buf[0];
	
	return buf[0];
}
//-----------------------------------------------------------------------
int syn_swallow_comments()
{
// read input until '*/'
// return number of chars

	int count=0;
	char prev_char = 0;
	int c;

	while( get_next_char(&c) != EOF)
	{
		count++;
	
		if(c == '/' && prev_char == '*')
			break;
		
		prev_char = c;
	}
	
	return count;
}
